import React, { useEffect } from 'react';
import axios from 'axios';
import turkeyGeoJson from './turkey-provinces.json';
import MapComponent from './MapComponent';
import PredictForm from './PredictForm';
import ProvinceData from './ProvinceData';
import { DataProvider, useDataContext } from './DataContext';

function AppContent() {
  const {
    provinces, 
    setProvinces,
    selectedProvince, 
    setSelectedProvince, 
    provinceData, 
    loading, 
    setLoading,
    error, 
    setError,
    systemStatus,
    setSystemStatus,
    predictForm, 
    setPredictForm,
    handlePredictInputChange, 
    handlePredictSubmit, 
    resetPredictForm,
    predictLoading, 
    setPredictLoading,
    predictError, 
    setPredictError,
    predictResult,
    setPredictResult
  } = useDataContext();

  useEffect(() => {
    // İlleri yükle
    const fetchProvinces = async () => {
      try {
        setLoading(true);
        const response = await axios.get('http://localhost:5000/api/provinces');
        setProvinces(response.data);
        setLoading(false);
      } catch (err) {
        setError('İller yüklenirken bir hata oluştu');
        setLoading(false);
        console.error('İller yüklenirken hata:', err);
      }
    };

    fetchProvinces();

    // Sistem durumunu kontrol et
    const checkSystemStatus = async () => {
      try {
        const healthResponse = await axios.get('http://localhost:5000/api/health');
        const hadoopResponse = await axios.get('http://localhost:5000/api/hadoop/status');

        setSystemStatus({
          api: healthResponse.data,
          hadoop: hadoopResponse.data
        });
      } catch (err) {
        console.error('Sistem durumu kontrol edilirken hata:', err);
        setSystemStatus({
          error: 'Sistem durumu kontrol edilirken hata oluştu'
        });
      }
    };

    checkSystemStatus();
  }, [setLoading, setProvinces, setError, setSystemStatus]);

  // Predict form submit handler
  const handlePredictSubmitLocal = async (e) => {
    e.preventDefault();

    // Form validation
    if (!predictForm.soil_ph || !predictForm.rainfall_mm || !predictForm.temperature_celsius) {
      setPredictError('Lütfen tüm alanları doldurun');
      return;
    }

    try {
      setPredictLoading(true);
      setPredictError(null);

      const response = await axios.post('http://localhost:5000/api/predict', {
        soil_ph: parseFloat(predictForm.soil_ph),
        rainfall_mm: parseFloat(predictForm.rainfall_mm),
        temperature_celsius: parseFloat(predictForm.temperature_celsius)
      });

      setPredictResult(response.data);
      setPredictLoading(false);
    } catch (err) {
      setPredictError('Tahmin yapılırken bir hata oluştu');
      setPredictLoading(false);
      console.error('Predict error:', err);
    }
  };

  // Reset predict form
  const resetPredictFormLocal = () => {
    setPredictForm({
      soil_ph: '',
      rainfall_mm: '',
      temperature_celsius: ''
    });
    setPredictResult(null);
    setPredictError(null);
  };

  // Function to render weather icons
  const renderWeatherIcons = (icons) => {
    if (!icons || icons.length === 0) {
      return null;
    }
    return icons.map((icon, index) => (
      <img key={index} src={icon} alt="Weather icon" className="weather-icon"/>
    ));
  };

  return (
    <div className="App">
      <header className="App-header">
        <p>Türkiye haritasından bir il seçerek tarımsal verileri ve ürün tahminlerini görüntüleyin</p>
      </header>

      <div className="system-status">
        <h3>Sistem Durumu</h3>
        {systemStatus?.error ? (
          <p className="error">{systemStatus.error}</p>
        ) : (
          <div className="status-container">
            <div className="status-item">
              <span>API:</span>
              <span className={systemStatus?.api?.status === "healthy" ? "status-ok" : "status-error"}>
                {systemStatus?.api?.status === "healthy" ? "Çalışıyor" : "Hata"}
              </span>
            </div>
            <div className="status-item">
              <span>Hadoop:</span>
              <span className={systemStatus?.hadoop?.status === "connected" ? "status-ok" : "status-error"}>
                {systemStatus?.hadoop?.status === "connected" ? "Bağlı" : "Bağlantı Hatası"}
              </span>
            </div>
          </div>
        )}
      </div>

      <div className="container">
        <h2>Türkiye Tarım ve Hava Durumu Bilgi Sistemi</h2>
        <div className="map-container">
          <MapComponent turkeyGeoJson={turkeyGeoJson} />
        </div>
        <div className="data-container">
          <div className="province-selector">
            <h3>İl Seçimi</h3>
            <select onChange={(e) => setSelectedProvince(parseInt(e.target.value))} value={selectedProvince || ''}>
              <option value="">İl seçiniz</option>
              {provinces.map(province => (
                <option key={province.province_id} value={province.province_id}>
                  {province.province_name}
                </option>
              ))}
            </select>
          </div>

          {loading && <p>Yükleniyor...</p>}
          {error && <p className="error-message">{error}</p>}

          <ProvinceData renderWeatherIcons={renderWeatherIcons} />

          {/* Predict Form Section */}
          <PredictForm 
            handleSubmit={handlePredictSubmitLocal}
            resetForm={resetPredictFormLocal}
          />
        </div>
      </div>

      <footer className="App-footer">
        <p>Hadoop, Spark, Flask ve React ile geliştirilmiş Tarım Tahmin Uygulaması &copy; 2025</p>
      </footer>
    </div>
  );
}

function App() {
  return (
    <DataProvider>
      <AppContent />
    </DataProvider>
  );
}

export default App;
