import 'leaflet/dist/leaflet.css';
import turkeyGeoJson from './turkey-provinces.json';
import About from './About'; // Eğer About.js’iniz yoksa bu satırı kaldırın


// English -> Turkish map
const cropMap = {
  rice: 'Pirinç',
  maize: 'Mısır',
  chickpea: 'Nohut',
  kidneybeans: 'Barbunya',
  pigeonpeas: 'Bezelye',
  mungbean: 'Mung Fasulyesi',
  blackgram: 'Kara Fasulye',
  lentil: 'Mercimek',
  pomegranate: 'Nar',
  banana: 'Muz',
  mango: 'Mango',
  grapes: 'Üzüm',
  watermelon: 'Karpuz',
  coconut: 'Hindistan Cevizi',
  cotton: 'Pamuk',
  muskmelon: 'Kavun',
  apple: 'Elma'
};

// LabelEncoder.classes_ (alphabetical) -> index mapping
const cropClasses = [
  'apple', 'banana', 'blackgram', 'chickpea', 'grapes',
  'kidneybeans', 'lentil', 'maize', 'mango', 'muskmelon', 'coconut', 'cotton',
  'pomegranate', 'rice', 'watermelon'
];

function App() {
  const [provinces, setProvinces] = useState([]);
  const [selectedProvince, setSelectedProvince] = useState(null);
  const [provinceData, setProvinceData] = useState(null);
  const [loadingProvince, setLoadingProvince] = useState(false);
  const [errorProvince, setErrorProvince] = useState(null);
  const [systemStatus, setSystemStatus] = useState({});
  const [accuracy, setAccuracy] = useState(null);


  const [predictForm, setPredictForm] = useState({ soil_ph: '', rainfall_mm: '', temperature_celsius: '' });
  const [predictResult, setPredictResult] = useState(null);
  const [predictLoading, setPredictLoading] = useState(false);
  const [predictError, setPredictError] = useState(null);

  // Fetch provinces and system status
  useEffect(() => {
    axios.get('http://localhost:5000/api/provinces')
      .then(res => setProvinces(res.data))
      .catch(err => console.error('İller yüklenirken hata:', err));

    Promise.all([
      axios.get('http://localhost:5000/api/health'),
      axios.get('http://localhost:5000/api/hadoop/status'), 
      axios.get('http://localhost:5000/api/accuracy')
    ])
      .then(([healthRes, hadoopRes, accRes]) => {
        setSystemStatus({ api: healthRes.data, hadoop: hadoopRes.data });
        setAccuracy(accRes.data.accuracy);
      })
      .catch(err => {
        console.error('Sistem durumu yüklenirken hata:', err);
        setSystemStatus({ error: 'Sistem durumu alınamadı' });
      });
  }, []);

  // Fetch selected province data
  useEffect(() => {
    if (!selectedProvince) return;
    setLoadingProvince(true);
    axios.get(`http://localhost:5000/api/province/${selectedProvince}`)
      .then(res => {
        setProvinceData(res.data);
        setErrorProvince(null);
      })
      .catch(err => {
        console.error('İl verisi yüklenirken hata:', err);
        setErrorProvince('İl verisi yüklenirken hata oluştu');
      })
      .finally(() => setLoadingProvince(false));
  }, [selectedProvince]);

  const handleProvinceSelect = e => {
    setSelectedProvince(e.target.value);
    setProvinceData(null);
  };

  const handlePredictInputChange = e => {
    const { name, value } = e.target;
    setPredictForm(prev => ({ ...prev, [name]: value }));
  };

  const handlePredictSubmit = e => {
    e.preventDefault();
    const { soil_ph, rainfall_mm, temperature_celsius } = predictForm;
    if (!soil_ph || !rainfall_mm || !temperature_celsius) {
      setPredictError('Lütfen tüm alanları doldurun');
      return;
    }
    setPredictLoading(true);
    axios.post('http://localhost:5000/api/predict', {
      soil_ph: parseFloat(soil_ph),
      rainfall_mm: parseFloat(rainfall_mm),
      temperature_celsius: parseFloat(temperature_celsius)
    })
      .then(res => {
        setPredictResult(res.data);
        setPredictError(null);
      })
      .catch(err => {
        console.error('Predict error:', err);
        setPredictError('Tahmin başarısız');
      })
      .finally(() => setPredictLoading(false));
  };

  const resetPredictForm = () => {
    setPredictForm({ soil_ph: '', rainfall_mm: '', temperature_celsius: '' });
    setPredictResult(null);
    setPredictError(null);
  };

  const provinceStyle = feature => ({
    fillColor: selectedProvince == feature.properties.id ? '#4CAF50' : '#3388ff',
    weight: 2,
    opacity: 1,
    color: 'white',
    dashArray: '3',
    fillOpacity: 0.7
  });

  const onEachFeature = (feature, layer) => {
    layer.on({ click: () => setSelectedProvince(feature.properties.id) });
    layer.bindTooltip(feature.properties.name);
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Tarım Tahmin Uygulaması</h1>
        <p>Türkiye haritasından bir il seçerek verileri görüntüleyin</p>
      </header>
      <nav className="main-nav">
        <ul>
          <li><NavLink to="/" end>Ana Sayfa</NavLink></li>
          <li><NavLink to="/about">Bu Proje Ne İşe Yarar?</NavLink></li>
        </ul>
      </nav>
      <Routes>
        <Route path="/" element={
          <>  {/* Full-width map */}
            <div className="fullwidth-map">
              <MapContainer center={[39, 35]} zoom={6} className="map-container">
                <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
                <GeoJSON data={turkeyGeoJson}
                  style={f=>({ fillColor: selectedProvince==f.properties.id?'#4CAF50':'#3388ff', weight:2, color:'#fff', dashArray:'3', fillOpacity:0.7 })}
                  onEachFeature={(f,l)=>{l.on({click:()=>handleProvinceSelect({target:{value:f.properties.id}})});l.bindTooltip(f.properties.name);}}
                />
              </MapContainer>
            </div>
            <div className="container">  {/* panels */}
              <div className="province-selector">
                <h2>İl Seçimi</h2>
                <select value={selectedProvince} onChange={handleProvinceSelect}>
                  <option value="">-- Seçiniz --</option>
                  {provinces.map(p=><option key={p.province_id} value={p.province_id}>{p.province_name}</option>)}
                </select>
                {loadingProvince && <p>Yükleniyor...</p>}
                {errorProvince && <p className="error">{errorProvince}</p>}
              </div>
              {provinceData && (
                <div className="province-data">
                  <h2>{provinceData.province_name} İli Verileri</h2>
                  <div className="data-card">
                    <div className="data-item"><h3>Toprak pH</h3><p className="data-value">{provinceData.soil_ph}</p></div>
                    <div className="data-item"><h3>Yağış (mm)</h3><p className="data-value">{provinceData.rainfall_mm}</p></div>
                    <div className="data-item"><h3>Sıcaklık (°C)</h3><p className="data-value">{provinceData.temperature_celsius}</p></div>
                  </div>
                  <div className="prediction-card">
                    <h3>Önerilen Ürün</h3>
                    <p className="prediction">{cropMap[cropClasses[provinceData.recommended_crop]]}</p>
                  </div>
                </div>
              )}
              {/* Predict Form Section */}
              <div className="predict-section">
                <h2>Manuel Tahmin Yap</h2>
                <p>Toprak pH, yağış ve sıcaklık değerlerini girerek hangi ile uygun olduğunu öğrenin</p>
                <form onSubmit={handlePredictSubmit} className="predict-form">
                  <div className="form-group">
                    <label htmlFor="soil_ph">Toprak pH:</label>
                    <input type="number" id="soil_ph" name="soil_ph" value={predictForm.soil_ph}
                      onChange={handlePredictInputChange} step="0.1" min="0" max="14" placeholder="Örnek: 6.5" required />
                  </div>
                  <div className="form-group">
                    <label htmlFor="rainfall_mm">Yağış (mm):</label>
                    <input type="number" id="rainfall_mm" name="rainfall_mm" value={predictForm.rainfall_mm}
                      onChange={handlePredictInputChange} step="0.1" min="0" placeholder="Örnek: 500" required />
                  </div>
                  <div className="form-group">
                    <label htmlFor="temperature_celsius">Sıcaklık (°C):</label>
                    <input type="number" id="temperature_celsius" name="temperature_celsius" value={predictForm.temperature_celsius}
                      onChange={handlePredictInputChange} step="0.1" placeholder="Örnek: 25" required />
                  </div>
                  <div className="form-buttons">
                    <button type="submit" className="predict-button" disabled={predictLoading}>{predictLoading? 'Tahmin Yapılıyor...' : 'Tahmin Yap'}</button>
                    <button type="button" onClick={resetPredictForm} className="reset-button">Temizle</button>
                  </div>
                </form>
                {predictError && <p className="error">{predictError}</p>}
                {predictResult && (
                  <div className="predict-result">
                    <h3>Tahmin Sonucu</h3>
                    <div className="result-card">
                      <div className="result-item"><h4>Önerilen İl:</h4><p className="result-value">{predictResult.predicted_province_name}</p></div>
                      <div className="result-item"><h4>İl ID:</h4><p className="result-value">{predictResult.predicted_province_id}</p></div>
                      <div className="input-summary"><h4>Girilen Değerler:</h4>
                        <p>pH: {predictResult.input_data.soil_ph}</p>
                        <p>Yağış: {predictResult.input_data.rainfall_mm} mm</p>
                        <p>Sıcaklık: {predictResult.input_data.temperature_celsius} °C</p>
                      </div>
                    </div>
                  </div>
                )}
              </div>
            </div>
          </>
        } />
        <Route path="/about" element={<About />} />
      </Routes>
      <footer className="App-footer">
        <p>Hadoop, Spark, Flask &amp; React ile Tarım Tahmin © 2025</p>
      </footer>
    </div>
  );
}
