// App.js
import React from 'react';
import DataProvider, { useDataContext } from './DataContext';
import MapComponent from './MapComponent';
import PredictForm from './PredictForm';
import ProvinceData from './ProvinceData';
import turkeyGeoJson from './turkey-provinces.json';
import './App.css';

// Inner app that consumes context
function Main() {
  const {
    provinces,
    selectedProvince,
    setSelectedProvince,
    provinceData,
    loading,
    error,
    systemStatus,
    predictForm,
    handlePredictInputChange,
    handlePredictSubmit,
    resetPredictForm,
    predictLoading,
    predictError,
    predictResult
  } = useDataContext();

  React.useEffect(() => {
    // fetch provinces
    const fetchProvinces = async () => {
      try {
        const res = await fetch('http://localhost:5000/api/provinces');
        const data = await res.json();
        setProvinces(data);
      } catch {
        setError('İller yüklenirken bir hata oluştu');
      }
    };

    // check system status
    const checkSystemStatus = async () => {
      try {
        const [apiRes, hadoopRes] = await Promise.all([
          fetch('http://localhost:5000/api/health'),
          fetch('http://localhost:5000/api/hadoop/status')
        ]);
        setSystemStatus({
          api: await apiRes.json(),
          hadoop: await hadoopRes.json()
        });
      } catch {
        setSystemStatus({ error: 'Sistem durumu kontrol edilirken hata oluştu' });
      }
    };

    fetchProvinces();
    checkSystemStatus();
  }, [setProvinces, setError, setSystemStatus]);

  return (
    <div className="App retro-theme">
      <header className="App-header">
        <p>Türkiye haritasından bir il seçerek tarımsal verileri ve ürün tahminlerini görüntüleyin</p>
      </header>

      <section className="system-status">
        <h3>Sistem Durumu</h3>
        {systemStatus.error ? (
          <p className="error">{systemStatus.error}</p>
        ) : (
          <div className="status-container">
            <div className="status-item">
              <span>API:</span>
              <span className={systemStatus.api?.status === 'healthy' ? 'ok' : 'error'}>
                {systemStatus.api?.status === 'healthy' ? 'Çalışıyor' : 'Hata'}
              </span>
            </div>
            <div className="status-item">
              <span>Hadoop:</span>
              <span className={systemStatus.hadoop?.status === 'connected' ? 'ok' : 'error'}>
                {systemStatus.hadoop?.status === 'connected' ? 'Bağlı' : 'Hata'}
              </span>
            </div>
          </div>
        )}
      </section>

      <main className="container">
        <h2>Türkiye Tarım ve Hava Durumu Bilgi Sistemi</h2>
        <div className="map-wrapper">
          <MapComponent turkeyGeoJson={turkeyGeoJson} className="map retro-border" />
        </div>

        <aside className="data-panel">
          <div className="selector">
            <label>İl seçiniz:</label>
            <select
              value={selectedProvince || ''}
              onChange={e => setSelectedProvince(Number(e.target.value))}
            >
              <option value="">--</option>
              {provinces.map(p => (
                <option key={p.province_id} value={p.province_id}>{p.province_name}</option>
              ))}
            </select>
            {loading && <p>Yükleniyor...</p>}
            {error && <p className="error">{error}</p>}
          </div>

          <ProvinceData />

          <PredictForm />
        </aside>

        <footer className="App-footer retro-border">
          <p>Hadoop, Spark, Flask & React ile geliştirilmiş Tarım Tahmin Uygulaması © 2025</p>
        </footer>
      </main>
    </div>
  );
}

// Root App wraps everything in context
export default function App() {
  return (
    <DataProvider>
      <Main />
    </DataProvider>
  );
}

